#include <vector> 
using namespace std; 
class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int N = colors.size(); 
        int count = 0; 

        if (k == 1) { 
            return N; 
        } 

        int a = 1; 
        int i = 1; 
        while (i < N + k - 1) { 
            int aa = i; 
            int bb = i - 1; 

            if (aa >= N) { 
                aa -= N; 
            } 
            if (bb >= N) { 
                bb -= N; 
            } 

            if (colors[aa] != colors[bb]) { 
                ++a; 
            } else { 
                a = 1; 
            } 

            if (a >= k) { 
                ++count; 
            } 

            ++i; 
        } 

        return count; 
    } 
};
