class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int cnt = 1;
        int res = 0;
        int n = c.size();
        for (int i = 1; i < n + k - 1; i++) {
            if (c[i % n] != c[(i + 1) % n]) {
                cnt++;
            } else {
                cnt = 1;
            }
            res += (cnt >= k);
        }
        return res;
    }
};
