class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0, ap = 0;
        vector <int> temp;
        auto fun = [&](int x, int y){
            return colors[x%n] != colors[y%n];
        };
        for(int i = 0; i < k-1; i++)if(fun(i, i+1))ap++;
        if(ap == k-1)ans++;
        for(int i = 1; i < n; i++){
            if(fun(i-1, i))ap--;
            if(fun(i+k-2, i+k-1))ap++;
            if(ap == k-1)ans++;
        }
        return ans;
    }
};
