class Solution 
{
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        int n = colors.size(), ans = 0, left = 0, right = 0;
        while(left < n)
        {
            if(colors[right % n] == colors[(right + 1) % n])
            {
                left = right + 1;
            }
            right++;
            if(right - left + 1 == k)
            {
                ans++;
                left++;
            }
            // cout << left << ' ' << right << ' ' << ans << '\n';
        }
        // cout << "-----------------------------------------------\n";
        return ans;
    }
};
