func numberOfAlternatingGroups(colors []int, k int) int {
    n := len(colors)
    l, r, f, res := 0, 1, true, 0
    for l < n {
        switch {
            case f && r - l < k:
            f = colors[r % n] != colors[(r - 1) % n]
            r++
            case f:
            res++
            l++
            default:
            l = r - 1
            f = true
        }
    }
    return res
}
