class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<n;i++) colors.push_back(colors[i]);
        vector<int> pre(2*n,0);
        for(int i = 1;i<2*n;i++){
            pre[i] = colors[i]!=colors[i-1];
        }
        // for(int x:pre) cout<<x<<" ";
        // cout<<endl;
        for(int i =1;i<2*n;i++) pre[i] += pre[i-1];
        int ans = 0;
        for(int i =0;i<n;i++){
            int sm = pre[i+k-1] - pre[i];
            if(sm == k-1) ans ++;
        }      
        return ans;
    }
};
