class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k == 1) {
            return n;
        }
        int count = 0;
        int low = 0, high = 1;
        int windowLength = 1;
        while (high < n + k - 1) {
            int curr = high % n;
            int prev = (high - 1) % n;
            if (colors[curr] != colors[prev]) {
                windowLength++;
            } 
            else {
                windowLength = 1;
            }

            if (windowLength >= k) {
                count++;
            }

            high++;
        }
        return count;
    }
};
