class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        deque<pair<int,bool>>q;
        int count = 1;
        q.push_back({colors[0],true});
        for(int i = 1;i<k;i++){
            q.push_back({colors[i],(colors[i]!=q.back().first)});
            count+=q.back().second;
        }

        int ans = 0;
        if(count==k)ans++;
        for(int i = 1;i<colors.size();i++){
            auto p = q.front();
            q.pop_front();
            count-=p.second;

            p = q.front();
            q.pop_front();
            count-=p.second;
            q.push_front({p.first,true});
            count++;

            q.push_back({colors[(i+k-1)%colors.size()],(colors[(i+k-1)%colors.size()]!=q.back().first)});
            count+=q.back().second;
            ans+=(count==k);
        }
        return ans;
    }
};
