class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
            int n=colors.size();
        vector<int> dp(2*n-1);
        dp[0]=1;
        int ans=0;
        for(int i=1;i<2*n-1;i++)
        {
            if(colors[i%n]==colors[(i+n-1)%n])
            dp[i]=1;
            else dp[i]=dp[i-1]+1;
        }
        for(int i=n-1;i<2*n-1;i++)
        {
            if(dp[i]>=k)
            ans++;
        }
        return ans;
    }
};
