class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(),len=0,res=0;
        for(int i=0;i<n+k-1;i++){
            if(i==0 || colors[i%n]!=colors[(i-1)%n]){
                len++;
            }
            else{
                len=1;
            }
            if(len>=k){
                res++;
            }
        }
        return res;
    }
};
