class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int alternateLen = 1;
        int ans = 0;
        for(int i = 1; i < n + k - 1; i++) {
            if(colors[i % n] != colors[(i - 1 + n) % n])
                alternateLen++;
            else
                alternateLen = 1;
            if(alternateLen >= k)
                ans++;
        }
        return ans;
    }
};
