class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        int cnt = 0;
        if (k==1) return n;
        int len = 1;
        int i = 1;
        for(int i=1;i<n+k-1;i++){
            int curr = i;
            int prev = i-1;
            if (curr>=n){
                curr-=n;
            }
            if (prev>=n){
                prev-=n;
            }
            if (colors[curr]!=colors[prev]){
                len++;
            }
            else{
                len = 1;
            }
            if (len>=k){
                cnt++;
            }
        }
        return cnt; 
    } 
};
