class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {

    int count = 0;
    for(int i=0;i<k-1;i++){
        col.push_back(col[i]);
    }
         int n = col.size();
        vector<int>dp(n+10);

        dp[0]=1;

        for(int i=1;i<n;i++){
            if(col[i]!=col[i-1]) dp[i]=dp[i-1]+1;
            else dp[i]=1;
        }

        int ans=0;

        for(int i=0;i<n;i++){
            if(dp[i]<k) continue;
            ans++;
        }


    return ans;
    }
};
