class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int len = colors.size(), ans = 0;
        for (int left = 0, right = 0; right <= len + k - 2; right++) {
            if (left != right && colors[right % len] == colors[(right - 1) % len])
                left = right;
            if (right - left + 1 > k)
                left++;
            if (right - left + 1 == k)
                ans++;
        }
        return ans;
    }
};
