class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& C, int k) {
        C.insert(C.end(), C.begin(), C.begin() + k - 1);

        int R = 0, N = 1;

        for (size_t i = 1; i < C.size(); ++i) {
            N = (C[i] != C[i - 1]) ? N + 1 : 1;
            if (N >= k) {
                ++R;
            }
        }

        return R;
    }
};
