class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors[:k - 1]
        for pos in range(1, len(colors), 2):
            colors[pos] ^= 1

        pos = result = 0
        while pos < len(colors) - k:
            while pos < len(colors) - 1 and colors[pos] != colors[pos + 1]:
                pos += 1

            start = pos
            while pos < len(colors) - 1 and colors[pos] == colors[pos + 1]:
                pos += 1

            size = pos - start + 1
            result += max(size - k + 1, 0)

        return result
