class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for(int i = 0 ; i < n ; i++) c.push_back(c[i]);
        vector<int> p = {1};
        for(int i = 1 ; i < 2 * n ; i++) p.push_back(p.back() + (c[i] != c[i-1]));
        int cnt = 0;
        for(int i = 0 ; i < n ; i++){
            cnt += (p[i + k - 1] - p[i] + 1 == k);
        }
        return cnt;
    }
};
