#include <vector> 
using namespace std; 

class Solution { 
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;

        if (k == 1) return n;

        int len = 1, i = 1;
        while (i < n + k - 1) {
            int val = i, temp = i - 1;

            // Handle circular indexing
            if (val >= n) val -= n;
            if (temp >= n) temp -= n;

            if (colors[val] != colors[temp]) ++len;
            else len = 1;

            if (len >= k) ++count;

            i++;
        }
        return count;
    }
};
