class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(),ans=0;
        deque<int> q;
        for(int j=0;j<n+k-1;j++){
            int i=j%n;
            if(q.size() and q.back()!=colors[i]^1)
                q.clear();
            if(q.size()>k-1) q.pop_front();
            q.push_back(colors[i]);
            if(j>=k-1) ans+=(q.size()==k);
        }
        return ans;
    }
};
