class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const int n = (int)colors.size();
        vector<int> dp(2*n,1);
        for(int i=1;i<2*n;++i) {
            if(colors[i%n] != colors[(i-1)%n]) dp[i] = 1 + dp[i-1];
            else dp[i] = 1;
        }
        int ans = dp[k-1] == k ;
        for(int i=k;i<2*n && i < k + n-1;++i)ans += dp[i] >= k ;

        return ans;
    }
};
