class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }

        int ans = 0, curr = 0;
        for (int i = 1; i < k; i++) {
            curr += colors[i] != colors[i - 1];
        }
        ans += curr == k - 1;
        for (int i = k; i < colors.size(); i++) {
            curr += colors[i] != colors[i - 1];
            curr -= colors[i - k] != colors[i - k + 1];
            ans += curr == k - 1;
        }
        return ans;
    }
};
