class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
        int[] arr = new int[c.length + k - 1];
        System.arraycopy(c, 0, arr, 0, c.length);
        System.arraycopy(c, 0, arr, c.length, k - 1);
        int ans = 0, number = 1;
        for (int i = 1; i < arr.length; ++i) {
            if (arr[i] != arr[i - 1]) {
                number++;
            } else {
                number = 1;
            }
            if (number >= k) {
                ans++;
            }
        }
        return ans;
    }
}
