class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        for(int i=0;i<k-1;i++){
            c.push_back(c[i]);
        }
        vector<int>p;
        for(int i=1;i<c.size();i++){
            if(i<n){
                if(c[i]!=c[i-1]) p.push_back(1);
                else p.push_back(0);
            }else{
                if(c[i]!=c[i-1]) p.push_back(1);
                else break;
            }
        }
        // for(int x:p) cout<<x<<" ";
        // cout<<endl;
        int ct=0;
        int ans=0;
        for(int i=0;i<p.size();i++){
            if(p[i]==1) ct++;
            else{
                if(ct>=(k-1)){
                    ans+=(ct-k+2);
                }
                ct=0;
            }
        }
        if(ct>=(k-1)){
            ans+=(ct-k+2);
        }
        return ans;
    }
};
