class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        int n = color.size();
        int ans = 0;
        vector<int> pre(n+1, 1);
        pre[0] = 0;

        for(int i = 1; i < n; i++)
        {
            if(color[i] != color[i-1])
            {
                pre[i+1] = pre[i] + 1;
            }
            // cout << pre[i+1] << " ";
        }
        if(color[0] != color[n-1])
        {
            for(int i = 0; i < k-1; i++)
            {
                if(pre[i+1] == i+1)
                {
                    if((pre[i+1] + pre[n]) >= k) ans++;
                }
            }
        }
        for(int i = k-1; i < n; i++)
        {
            if(pre[i+1] >= k) ans++;          
        }
        return ans;
    }
};
