#include <bits/stdc++.h>
using namespace std;
#define pb push_back
#define ff first
#define ss second
#define pii pair<int, int>
#define ll long long int
#define vll vector<ll>
#define all(c) c.begin(), c.end()
const long long mod = 1e9 + 7;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();

        int cnt = 0;
        vll pre(n, 0);

        for (int l = 1; l < n; l++) {
            pre[l] += (c[l] != c[l - 1]) + pre[l - 1];
        }

        for (int r = k-1; r < n; r++) {
            if(pre[r] - pre[r - k + 1] == k-1)
                cnt++;
        }

        vector<int> c1 = c;
        for(int i = 0; i < k-1; i++) 
            c1.push_back(c[i]);

        for (int l = n; l < c1.size(); l++) 
            pre.push_back((c1[l] != c1[l - 1]) + pre[l - 1]);

        for (int r = n; r < c1.size(); r++) {
            if(pre[r] - pre[r - k + 1] == k-1)
                cnt++;
        }
        return cnt;
    }
};
