class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int> vec(n + n);

        for (int i = 0; i < n + n; i++) {
            vec[i] = colors[i % n];
        }

        vector<int> suff(n + n);

        for (int i = n + n - 1; i >= 0; i--) {
            suff[i] = 1;

            if (i + 1 < n + n && vec[i] != vec[i + 1]) {
                suff[i] += suff[i + 1];
            }
        }

        int res = 0;

        for (int i = 0; i < n; i++) {
            res += suff[i] >= k;
        }

        return res;
    }
};
