using ll = long long;
using vi = vector<ll>;
using vvi = vector<vi>;
using pii = pair<ll,ll>;
using vp = vector<pii>;
using ti3 = tuple<ll,ll,ll>;
using vs = vector<string>;

template<typename T>
using min_pq = priority_queue<T, vector<T>, greater<T>>;
template<typename T>
using max_pq = priority_queue<T>;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& aa, int k) {
        ll n = aa.size();
        // if (k==n) {
        //     bool ok = true;
        //     for (int i=1; i<n; i++) {
        //         if (aa[i] == aa[i-1]) ok = false;
        //     }
        //     int ans = 0;
        //     if (ok && (n%2 == 0)) ans = 1;
        //     return ans;
        // }

        ll cnt = 0;
        vi a;
        for (auto x : aa) a.push_back(x);
        for (auto x : aa) a.push_back(x);

        for (int i=1; i<k; i++) {
            if (a[i] != a[i-1]) cnt++;
        }

        ll ans = 0;
        if (cnt == k-1) ans++;
        for (int i=1; i<n; i++) {
            if (a[i] != a[i-1]) cnt--;
            if (a[i+k-1] != a[i+k-2]) cnt++;
            if (cnt == k-1) ans++;
        }
        return ans;

    }
};
