class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        arr = colors + colors[:k - 1]

        ans = i = 0
        n = len(arr)
        c = 0

        while i < n:
            if c == 0 or arr[i] == arr[i - 1]:
                c = 1
                i += 1
                continue

            if c == k:
                ans += 1
                i += 1
                continue

            c += 1
            if c == k:
                ans += 1
            i += 1

        return ans
