class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cc, int k) {
        int n=cc.size();
        for(int i=0;i<n;i++)cc.push_back(cc[i]);
        int i=0,ans=0;
        while(i<n){
            int x=cc[i],j=i;;
            i++;
            while(i<cc.size() && cc[i]!=x){
                x=cc[i];
                i++;
            }
            // cout<<max(0,min(i,n+k-1)-j-k+1)<<" "<<i<<" "<<j<<" "<<min(i,n+k-1)<<endl;
            ans+=max(0,min(i,n+k-1)-j-k+1);
        }
        return ans;
    }
};
