class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
      int n = colors.size();

    vector<int> nums = colors;
    for (int i = 0; i < k - 1; ++i) {
        nums.push_back(colors[i]);
    }

    int ans = 0;
    int num = 1; 

    for (int i = 1; i < nums.size(); ++i) {
        if (nums[i] != nums[i - 1]) {
            num++;
        } else {
            num= 1;
        }

        if (num >= k) {
            ans++;
        }
    }

    return ans;
    }
};
