class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        for(int i=0;i<k-1;i++){
            v.push_back(v[i]);
        }
        int n = v.size();
        int ans = 0;
        int i = 0;
        int len = 0;
        int prev = -1;
        while(i<n){
            int cur = v[i];
            if(prev == -1 || prev == 1-cur){
                if(len < k){
                    len++;
                }
            }else{
                len = 1;
            }
            ans += (len == k);
            prev = v[i];
            i++;
        }
        return ans;
    }
};
