class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int sz=1;
        int last=v[0];
        int ans=0;
        int n=v.size();
        for(int i=1;i<n+k-1;i++)
        {
            if(v[i%n]==v[(i-1+n)%n])
            {
                // cout<<"p at i="<<i<<endl;
                sz=1;
                last=v[i%n];
            }
            else 
            {
                last=1-last;
                sz+=1;
                if(sz>=k) 
                {
                    ans +=1;
                    // cout<<"i="<<i%n<<endl;
                }
            }
        }
        return ans;
    }
};
