class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        int ans=0;
        vector<int>v;
        for(int i=1;i<colors.size();i++){
            if(colors[i]!=colors[i-1]){
                v.push_back(0);
            }
            else{
                v.push_back(1);
            }
        }
        int cnt=0;
        for(int i=0;i<v.size();i++){
            if(v[i]==0){
                cnt++;
            }
            else{
                ans+=max(0,cnt-k+2);
                cnt=0;
            }
        }
        ans+=max(0,cnt-k+2);
        return ans;
    }
};
