class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        test = colors + colors[:k - 1]
        alternate = [(x != y) for x, y in zip(test, test[1:])]

        psum = [0]
        for x in alternate:
            psum.append(psum[-1] + x)

        return sum((y - x == k - 1) for x, y in zip(psum, psum[k - 1:]))
        return 0
