public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        var q = new List<int>();
        var reps = 0;
        var ret = 0;
        for (var i = 0; i < k; i++) {
            if (q.Any() && q.Last() == colors[i]) reps++;
            q.Add(colors[i]);
        }
        //if (reps == 0) ret++;

        for (var i = 0; i < colors.Length; i++) {
            if (q.Last() == colors[(i + k) % colors.Length]) reps++;
            q.Add(colors[(i + k) % colors.Length]);
            if (q[0] == q[1]) reps--;
            q.RemoveAt(0);
            if (reps == 0) ret++;
            //Console.WriteLine($"i: {i}; reps: {reps}; ret: {ret}");
        }
        return ret;
    }
}
