func numberOfAlternatingGroups(colors []int, k int) int {
    n := len(colors)
    colors = append(colors, colors...)
    ans := 0
    l, r := 0, 1
    for ;r < n + k - 1; r++ {
        if colors[r] == colors[r-1]{
            l = r
        } else {
            if r-l+1>=k {
                ans++
            }
        }
    } 
    return ans
}
