class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        int n = colors.size();
        for (int i = 0; i < n; ++i) {
            colors.push_back(colors[i]);
        }
        int l = 0, r = 0;
        int ans = 0;
        while (l < n) {
            if (r <= l) r = l + 1;
            while (r < l + k && colors[r] != colors[r - 1]) {
                ++r;
            }
            ans += max((r - l) - k + 1, 0);
            ++l;
        }
        return ans;
    }
};
