class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int n = colors.size();
        int m = n*2;
        for(int i = 0;i<n;i++){
            colors.push_back(colors[i]);
        }
        int dp[m+1];
        dp[0] = 0;
        dp[1] = 0;
        for(int i = 1 ;i<m;i++){
            if(colors[i]==colors[i-1]){
                dp[i+1] = dp[i];
            }
            else{
                dp[i+1] = dp[i]+1;
            }
        }
        // for(int i = 0 ;i<m;i++){
        //     cout<<dp[i]<<' ';
        // }
        // cout<<endl;
        for(int i = 0 ;i<n;i++){
            int cur = dp[i+k]-dp[i+1];
            // cout<<cur<<' ';
            if(cur==k-1){
                ans++;
            }
        }
        cout<<endl;
        return ans;
    }
};
