class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):
        colors += colors[:k - 1]  # Extend colors array as per the JavaScript implementation

        R = 0
        N = 1

        for i in range(1, len(colors)):
            if colors[i] != colors[i - 1]:
                N += 1
            else:
                N = 1

            if N >= k:
                R += 1

        return R
