class Solution {
public:
    void f(vector<int>& colors){
        int x = colors.size();
        int ans = 0;
        for(int i=0; i<x; i++){
            if(i==0){
                if(colors[i]!=colors[i+1] && colors[i] != colors[x-1]){
                    ans++;
                }
            }
            else if(i==x-1){
                if(colors[i]!=colors[i-1] && colors[i] != colors[0]){
                    ans++;
                }
            }
            else{
                if(colors[i]!=colors[i-1] && colors[i]!=colors[i+1]){
                    ans++;
                }
            }

        }
        // return ans;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int x = colors.size(); 
        int cnt = 0; 

        if (k == 1) { 

            return x; 
        } 

        int kl = 1; 
        int i = 1; 
        while (i < x + k - 1) { 
            int ok = i; 
            int bye = i - 1; 


            if (ok >= x) { 
                ok -= x; 
            } 
            if (bye >= x) { 
                bye -= x; 
            } 

            if (colors[ok] != colors[bye]) { 
                ++kl; 
            } else { 
                kl = 1; 
            } 


            if (kl >= k) { 
                ++cnt; 
            } 

            ++i; 
        } 

        return cnt; 
    }
};
