class Solution {
public:
    int check (int a,int b,int c)
    {
        return (b!=a&&c!=b);
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        map<int,int>m;
        k-=2;
        int ans=0;
        for(int i=0;i<k;i++)
        {
            int ne=(i+1)%colors.size();
            int pr=(i-1+colors.size())%colors.size();
            m[check(colors[ne],colors[i],colors[pr])]++;

        }
    if(m[1]==k)
        ans++;
        for(int i1=1;i1<colors.size();i1++)
        {
            int i=i1-1;
             int ne=(i+1)%colors.size();
            int pr=(i-1+colors.size())%colors.size();
            m[check(colors[ne],colors[i],colors[pr])]--;
            i=(i1+k-1)%colors.size();
            ne=(i+1)%colors.size();
            pr=(i-1+colors.size())%colors.size();
             m[check(colors[ne],colors[i],colors[pr])]++;
             if(m[1]==k)
        ans++;


        }
        return ans;




    }
};
