class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int i=0,j=1;
        int cnt=1;
        vector<int>colors=arr;
        for(auto it:arr){
            colors.push_back(it);
        }
        int ans=0;

        while(j<colors.size() && i<arr.size()){
        if(colors[j]!=colors[j-1]){
            cnt++;
        }else{
            i=j;
            j++;
            cnt=1;
            continue;
        }
            if(j-i+1 == k){
                ans++;
                i++;
            }
            j++;
        }
        return ans;
    }
};
