from typing import List

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        ans = 0
        cnt = 0
        a = colors + colors
        j=1

        for i in range(1, 2*n-1):
            if a[i] != a[i-1] and a[i] != a[i+1]:
                cnt += 1

            if i >= k-2:

                if cnt == k-2 and j<=n:
                    ans += 1

                if a[j] != a[j-1] and a[j] != a[j+1]:
                    cnt -= 1

                j += 1


        return ans
