class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& ori, int k) {
        int count = 1,ans = 0;
        int on = ori.size();
        vector<int>arr;
        for(int i=k-2;i>=0;i--){
            arr.push_back(ori[on-1-i]);
        }
        for(int i=0;i<on;i++){
            arr.push_back(ori[i]);
        }
        int n = arr.size();
        bool col = arr[0];
        for(int i=0;i<k-1;i++){
            arr.push_back(ori[i]);
        }
        // for(auto it:arr){
        //     cout<<it<<" ";
        // }
        for(int i=1;i<n;i++){
            if(col!=arr[i]){
                count++;
            }
            else{
                if(count>=k){
                    ans+=(count-k+1);
                }
                count = 1;
            }
            col = arr[i];
            // cout<<count<<endl;
        }
        if(count>=k){
            // cout<<"idhr"<<endl;
            ans+=(count-k+1);
        }
        return ans;
    }
};
