class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& vec, int k) {
        int n = vec.size();
        int ans = 0, lp = 0;

        while (lp < n) {
            int len = 1;
            while (len < 3*n && vec[(lp + len)%n] != vec[(lp+len+n-1)%n]) ++len;
            ans += min(n - lp, max(0, len - k + 1));
            lp += len;
        }

        return ans;
    }
};
