class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], group_size: int) -> int:
        total_elements = len(colors)
        consecutive_count = 0


        for i in range(1, group_size):
            if colors[i] == colors[i - 1]:
                consecutive_count += 1

        alternating_groups = 0


        if consecutive_count == 0:
            alternating_groups += 1


        for start_index in range(total_elements - 1):
            end_index = (start_index + group_size) % total_elements

            if colors[end_index] == colors[end_index - 1]:
                consecutive_count += 1
            if colors[start_index] == colors[(start_index + 1) % total_elements]:
                consecutive_count -= 1

            if consecutive_count == 0:
                alternating_groups += 1

        return alternating_groups
