class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> a(colors);
        for(auto& x : colors) a.emplace_back(x);
        int ans = 0;
        for(int l=0,r=1;r<=n+k-2;++r){
            if(a[r] == a[r-1]){
                l = r;
            }
            if(r-l+1 >= k) ++ans;
        }
        return ans;
    }
};
