func numberOfAlternatingGroups(colors []int, k int) int {
    n := len(colors)
	colors = append(colors, colors...)
	res := 0
	pre := -1
	t := 0
	for i := 0; i < n+k-1; i++ {
		if pre == -1 {
			t += 1
		} else if pre ^ colors[i] == 1 {
			t += 1
		} else {
            res += max(0, t-(k-1))
			t = 1
		}
		pre = colors[i] 
	}
    res += max(0, t-(k-1))

	return res 
}
