#include "bits/stdc++.h"
using namespace std;
#define ll long long



class Solution {
public:
	int numberOfAlternatingGroups(vector<int>& a, int k) {
		int n = a.size(), ans = 0;
		auto b = a;
		for(const auto &x: b) a.push_back(x);
		n *= 2;

		vector p(n, 0);
		iota(p.begin(), p.end(), 0);
		for(int i = 1; i < n / 2 + k - 1; i++) {
			if(a[i] != a[i - 1]) p[i] = p[i - 1];
            //cout << p[i] << " ";
			if(i - p[i] + 1 >= k) ans++;
		}
        cout << endl;
		return ans;
	}
};

