class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        l=0
        r=n
        ans = 0
        count = 0

        q = deque()
        for idx in range(n+k-1):
            i = colors[idx%n]
            if len(q)>=2:
                if not(q[-1]!=i and q[-2]==i):
                    t=q[-1]
                    q=deque()
                    if t!=i:
                        q.append(t)
            elif len(q)==1:
                if q[-1]==i:
                    q=deque()
            q.append(i)
            if len(q)>=k:
                # print(idx,q)
                count+=1

            while len(q)>=k:
                q.popleft()
        return count
